// 当用户回到本界面时，resume事件会被触发
ui.emitter.on("resume", function() {
    // 此时根据无障碍服务的开启情况，同步开关的状态
   if(auto.service != null){
        ui.card1.setVisibility(8);
        ui.card2.setVisibility(0);
   }else{
       ui.card1.setVisibility(0);
       ui.card2.setVisibility(8);
   }
   if(checkPermission() != false){
       ui.card3.setVisibility(8);
       ui.card4.setVisibility(0);
   }else{
       ui.card3.setVisibility(0);
       ui.card4.setVisibility(8);
   }


   let sn_ = storage.get("sn");
   if(sn_ =="bbnc_off"){
      switch_script(ui.bbnc,ui.bbnc1)
      storage.put("sn","");
   }

});


//⚠️❌✔️
function msgbox(data) {
    let tipsbox_w=ui.tipsbox.visibility
    if(tipsbox_w == "8"){
    ui.run(() => {
    ui.tipstext.setText(data);
    ui.tipsbox.attr("visibility", "visible");
    let i = 0;
    let intervalId = setInterval(function() {
        if(i >= 70) {
            clearInterval(intervalId);
            // 在2秒后，h从100开始每隔10毫秒减少10，直到为0
            setTimeout(function() {
                let j = 70;
                let intervalId2 = setInterval(function() {
                    if(j <= 0) {
                        clearInterval(intervalId2);
                        ui.tipsbox.attr("visibility", "gone"); 
                    } else {
                        ui.tipsbox.attr("h", j.toString());
                        j -= 10
                        ;
                    }
                }, 10);
            }, 1500);
        } else {
            ui.tipsbox.attr("visibility", "visible"); 
            ui.tipsbox.attr("h", i.toString());
            i += 10;
        }
    }, 10); // 10毫秒*10次=100毫秒
    });
    }else{
        ui.run(() => {
            ui.tipstext.setText(data);
        });
    };
}


