main();

function check_s(){
    if (checkPermission() == false) {
        msgbox("⚠️ 开启悬浮窗权限！")
        return;
    }
    if (auto.service == null) {
        msgbox("⚠️ 开启无障碍服务！");
        return;
    }
    return true;
};


function switch_script(_item,_item1){
    let zt = _item.cardElevation;
    if(zt == 0){
        _item1.attr("cardBackgroundColor","#00FF54");
        _item.attr("cardElevation","5");
        return true
    }else{
        _item1.attr("cardBackgroundColor","#efefef");
        _item.attr("cardElevation","0");
        return false
    }

}

function stop_(){
    engines.all().map((ScriptEngine) => {
        if (engines.myEngine().toString() !== ScriptEngine.toString()) {
            ScriptEngine.forceStop();
        }
    });
};

function start_(sn,url_){
    msgbox("⏳ 正在载入脚本")
  threads.start(function() {
      try {
          let scriptName = sn; //脚本名
          http.__okhttp__.setTimeout(10000);
          let url = url_;
          let headers = {headers:{
              'AirScript-Token': astk,
              'Accept-Language': 'zh-cn,zh;q=0.5',
              'User-Agent': 'Mozilla/5.0(Macintosh;IntelMacOSX10_7_0)AppleWebKit/535.11(KHTML,likeGecko)Chrome/17.0.963.56Safari/535.11'
          }};
          let payload = {
              "Context": {
                  "argv": {
                      "neirong": "回调内容"
                  }
              }
          };
      r=http.postJson(url, payload, headers);
      res=r.body.string();
              if (JSON.parse(res).data.result.res != "") {
                  let codeStr = JSON.parse(res).data.result.res;
                  threads.shutDownAll;
                  ui.run(() => {
                      engines.execScript(scriptName, codeStr)
                  });
              } else {
                  toastLog("远程脚本连接超时");
              }
          } catch (err) {
              show_input();
              toastLog("内置脚本服务地址或密码错误");

          }

  });  
}

